    AND MBERNM = #{userName}



        AND UNITY_ID = #{userId}
        AND AUTH_MODE = 'M'
        AND EMAIL = #{email}




        AND EMAIL = #{email}


        AND MOBLPHON = #{mobileNumber}

SELECT
    UNITY_ID AS USERID
    , MBERNM AS  USERNAME
    , EMAIL
FROM TB_CT_UNITY_MBER
    WHERE 1=1
    AND MBERNM = #{userName}



        AND UNITY_ID = #{userId}
        AND AUTH_MODE = 'M'
        AND EMAIL = #{email}




