UPDATE TB_CT_COMPANY SET
    COMNAME = #{comName}
    , CEO = #{ceo}
    , ZIP = REPLACE(#{zip}, '-', '')
    , COM_REG_GB = #{comRegGb}
    , ADDR01 = #{addr01}
    , ADDR02 = #{addr02}
    , TEL = #{tel}
    , UPJONG_NM = #{upjongNm}
    , UPTEA_NM = #{upteaNm}
    , MT_ORG_CODE = #{mtOrgCode}
WHERE COMNO = #{comNo}
AND BIZNO = #{bizNo}
AND USE_AT = 'Y'

mapper.select:
SELECT COMNO , COMNAME , BIZNO , ZIP , ADDR01 , ADDR02 , CEO , TEL , FAX , UPJONG_NM AS BIZCND , UPTEA_NM AS INDUTY , COM_REG_GB
FROM TB_CT_COMPANY WHERE 1=1 AND USE_AT = 'Y' AND COMNAME LIKE '%'|| #{comName} ||'%'
AND BIZNO = #{bizNo}

SELECT COMNO , COMNAME , BIZNO , ZIP , SUBSTR(ZIP,0,3) AS ZIP1 , SUBSTR(ZIP,4,5) AS ZIP2 , ADDR01 , ADDR02 , CEO , TEL , FAX , UPJONG_NM AS BIZCND , UPTEA_NM AS INDUTY , COM_REG_GB
, (SELECT UNITY_ID FROM TB_CT_UNITY_MBER WHERE BIZNO = A.BIZNO AND COMNO = A.COMNO AND MT_GRADE_CODE = 'AA2000' ) AS COMPMNGR , MT_ORG_CODE FROM TB_CT_COMPANY A WHERE 1=1 AND USE_AT = 'Y' AND BIZNO = #{bizNo}
AND COMNAME = #{comNm}

mapper.insert:
INSERT INTO TB_CT_COMPANY (
    COMNO
    , COMNAME
    , COM_REG_GB
    , BIZNO
    , CEO
    , ZIP
    , ADDR01
    , ADDR02

    , TEL
    , UPTEA_NM
    , UPJONG_NM
    , REGDATE
    , USE_AT
    , MT_ORG_CODE
    ) VALUES (
     (SELECT NVL(MAX(COMNO)+1, 1) AS COMNO FROM TB_CT_COMPANY)
    , #{comName}
    , #{comRegGb}
    , #{bizNo}
    , #{ceo}
    , #{zip}
    , #{addr01}
    , #{addr02}

    , #{tel}
    , #{upteaNm}
    , #{upjongNm}
    , SYSDATE
    , 'Y'
    , #{mtOrgCode}
    )

