mapper.select:
SELECT NVL(MAX(UNITY_MBERNO),0) AS UNITY_MBERNO FROM TB_CT_NONMBER 
WHERE MBERNM = #{userName} 
AND MOBLPHON = #{moblphon}
AND BRTHDY = #{brthdy}

    	SELECT COUNT(*)
		FROM TB_CT_UNITY_MBER
		WHERE MBERNM = #{mbernm}
		AND BRTHDY = #{brthdy}

			AND REPLACE(MOBLPHON, '-', '') = #{moblphon}

		AND SECSN_AT = 'N'
		AND UNITY_ID = #{userId}

mapper.update:
    	UPDATE TB_CT_UNITY_MBER
		SET MBERNM = #{mbernm}
