mapper.select:
/* getLeExamQuizList */ SELECT ROW_NUMBER() OVER (ORDER BY QNO ASC) RNUM , EXAMNO, SEQ, QNO, QTYPE, FILE_PATH, QSUBJECT, QSUBJECT_FILE, A01, A01_FILE, A02, A02_FILE, A03, A03_FILE, A04, A04_FILE, A05, A05_FILE, RCODE, RTEXT, SUMMARY, WDATE, WRITER, USEYN, LEC_NUM, LEC_SEQ_NO, BUNBAN, EXAM_SEQ, TEST_SEQ , ALLOT FROM TB_LE_EXAM_QUIZ WHERE EXAMNO = #{examNo}
AND USEYN = 'Y'
AND USEYN = 'N'

SELECT NVL(SUM(ALLOT), 0) AS TOTAL_ALLOT FROM TB_LE_EXAM_QUIZ WHERE EXAMNO = #{examNo}
AND USEYN = 'Y'
AND USEYN = 'N'

mapper.update:

UPDATE TB_LE_EXAM_QUIZ SET 
	QTYPE = #{qtype}, FILE_PATH = #{FILE_path}, QSUBJECT = #{qsubject}, QSUBJECT_FILE = #{qsubjectFile}, 
	A01 = #{a01}, A01_FILE = #{a01File}, A02 = #{a02}, A02_FILE = #{a02File}, A03 = #{a03}, A03_FILE = #{a03File},
	A04 = #{a04}, A04_FILE = #{a04File}, A05 = #{a05}, A05_FILE = #{a05File}, 
	RCODE = #{rcode}, SUMMARY = #{summary}, WDATE = SYSDATE,  WRITER = #{writer}, USEYN = #{useyn} , RTEXT = #{rtext}, 	ALLOT = #{allot}
WHERE EXAMNO = #{examNo} 
AND QNO =   #{qno}
